    def autorizeLogin(self):
        if self.domain_lineEdit.text() == '':
            self.login_pushButton.setEnabled(False)
        elif self.server_lineEdit.text() == '':
            self.login_pushButton.setEnabled(False)
        elif self.usr_lineEdit.text() == '':
            self.login_pushButton.setEnabled(False)
        elif self.pwd_lineEdit.text() == '':
            self.login_pushButton.setEnabled(False)
        else :
            self.login_pushButton.setEnabled(True)
		
    def login(self):
        usr = self.usr_lineEdit.text()
        pwd = self.pwd_lineEdit.text()
        server = self.server_lineEdit.text()
        domain = self.domain_lineEdit.text()
        self.player = api(usr,pwd,server,domain)

    def saveuser(self):
        file = open('./ressources/login.config','a')
        usr = self.usr_lineEdit.text()
        pwd = self.pwd_lineEdit.text()
        server = self.server_lineEdit.text()
        domain = self.domain_lineEdit.text()
        newUser = usr+' '+pwd+' '+server+' '+domain
        file.write(newUser)
        file.close()
    
    def loaduser(self):
        file = open('./ressources/login.config','r')
        user = []
        for line in file:
            user.append([line.split(' ')])
        
        file.close()
        
